// Create node tables
CREATE NODE TABLE Person(id INT64, name STRING, age INT64, PRIMARY KEY(id));
CREATE NODE TABLE City(id INT64, name STRING, population INT64, PRIMARY KEY(id));

// Create relationship table
CREATE REL TABLE LivesIn(FROM Person TO City, since INT64);

// Insert data
CREATE (:Person {id: 1, name: 'Alice', age: 30});
CREATE (:Person {id: 2, name: 'Bob', age: 25});
CREATE (:Person {id: 3, name: 'Charlie', age: 35});
CREATE (:City {id: 1, name: 'Tokyo', population: 14000000});
CREATE (:City {id: 2, name: 'New York', population: 8300000});

// Create relationships
MATCH (p:Person), (c:City) WHERE p.id = 1 AND c.id = 1 CREATE (p)-[:LivesIn {since: 2020}]->(c);
MATCH (p:Person), (c:City) WHERE p.id = 2 AND c.id = 2 CREATE (p)-[:LivesIn {since: 2019}]->(c);
MATCH (p:Person), (c:City) WHERE p.id = 3 AND c.id = 1 CREATE (p)-[:LivesIn {since: 2018}]->(c);

// Query the data
MATCH (p:Person)-[l:LivesIn]->(c:City) RETURN p.name, c.name, l.since ORDER BY p.name;

// Find all people living in Tokyo
MATCH (p:Person)-[:LivesIn]->(c:City {name: 'Tokyo'}) RETURN p.name, p.age;

// Count people per city
MATCH (p:Person)-[:LivesIn]->(c:City) RETURN c.name, COUNT(p) as person_count;

