Office.onReady((info) => {
    if (info.host === Office.HostType.Outlook) {
        document.getElementById("getSubject").onclick = getSubject;
        document.getElementById("getSender").onclick = getSender;
        document.getElementById("getBody").onclick = getBody;
        document.getElementById("clearResult").onclick = clearResult;
    }
});

function getSubject() {
    const item = Office.context.mailbox.item;
    document.getElementById("result").innerText = "件名: " + item.subject;
}

function getSender() {
    const item = Office.context.mailbox.item;
    const from = item.from;
    document.getElementById("result").innerText = "送信者: " + from.displayName + " (" + from.emailAddress + ")";
}

function getBody() {
    const item = Office.context.mailbox.item;
    item.body.getAsync(Office.CoercionType.Text, (result) => {
        if (result.status === Office.AsyncResultStatus.Succeeded) {
            document.getElementById("result").innerText = "本文: " + result.value.substring(0, 500) + "...";
        }
    });
}

function clearResult() {
    document.getElementById("result").innerText = "";
}

